sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

	var gr = new GlideRecord("sys_user");
	gr.get(gs.getUserID());
	var city=gr.location.city;

	gs.info("BS: Fetching weather for: "+city);

    var restMessage = new sn_ws.RESTMessageV2();
    restMessage.setHttpMethod("get");
    restMessage.setEndpoint("http://api.openweathermap.org/data/2.5/weather?APPID=3aef82988ec564051f6a2a5d64fb1cd9&q=" +encodeURIComponent(city));
    restMessage.setRequestBody(JSON.stringify({}));
    var response = restMessage.execute();
    var responseBody = response.getBody();
    var responseObj = JSON.parse(responseBody);

    v_table.addRow({
        sys_id: gs.generateGUID(),
        city: responseObj.name,
        temperature: responseObj.main.temp,
        description: responseObj.weather[0].description
    });
})(v_table, v_query);
